#! /usr/bin/python# -*- coding:utf-8 -*-import requests, json, urllib.requestfrom com.forum.lottery.config.GlobalParams import GlobalConfigfrom com.forum.lottery.common.Singleton import Singleton# 请求头headers = dict()headers['User-Agent'] = 'SSCBeta'headers['client-version'] = GlobalConfig['Client_version']headers['x-requested-with'] = 'x-requested-with'headers['Content-Type'] = 'application/json'headers["Accept-Language"] = "zh-CN,zh;q=0.8"headers['sessionid'] = ''RequestUrl = 'url'RequestParams = 'params'RequestTime = 'requestTime'# requests封装class Request(object):    @classmethod    def get(cls, url, params):        headers['sessionid'] = Singleton().getSessionId()        try:            r = requests.get(GlobalConfig['DOMAIN'] + url, params = params, headers = headers)            r.encoding = 'UTF-8'            json_response = json.loads(r.text)            json_response[RequestTime] = r.elapsed.microseconds / 1000            json_response[RequestUrl] = url            json_response[RequestParams] = params            return 0, json_response        except Exception as e:            return 1, {'code':urllib.request.urlopen(GlobalConfig['DOMAIN'] + url).getcode(), 'msg': 'get请求出错，出错原因:%s' % e, RequestTime: r.elapsed.microseconds / 1000, RequestUrl: url}    @classmethod    def post(cls, url, params):        headers['sessionid'] = Singleton().getSessionId()        try:            if params is None:                data = None            elif isinstance(params, dict):                data = json.dumps(params)            else:                data = eval(params)            r = requests.post(GlobalConfig['DOMAIN'] + url, data, headers=headers)            json_response = json.loads(r.text)            json_response[RequestTime] = r.elapsed.microseconds / 1000            json_response[RequestUrl] = url            json_response[RequestParams] = data            return 0, json_response        except Exception as e:            return 1, {'code': urllib.request.urlopen(GlobalConfig['DOMAIN'] + url).getcode(), 'msg': 'post请求出错，出错原因:%s' %e, RequestTime: r.elapsed.microseconds / 1000, RequestUrl: url}